document.addEventListener("alpine:init",(()=>{Alpine.data("managerFrm",(()=>({modalStatus:new bootstrap.Modal(document.getElementById("modal-status")),setModalStatus(t,e){document.getElementById("title-modal-status").innerText=t,document.getElementById("desc-status-modal").innerText=e},data:[],loading:!1,buttonLabel:"Salvar",loadData(){this.loading=!0,fetch(document.getElementById("route_list").value).then((t=>t.json())).then((t=>{this.data=t,null!=t.name&&(document.getElementById("name").value=t.name),this.loading=!1}))},submitData(){if($("#frm").valid()){this.buttonLabel="Aguarde...",this.loading=!0;let t=null!==document.querySelector('input[name="_method"]')?document.querySelector('input[name="_method"]').value:"";this.data._method=t,this.data._token=document.head.querySelector("meta[name=csrf-token]").content,this.data.name=document.getElementById("name").value,fetch(document.getElementById("frm").action,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(this.data)}).then((t=>{1!=t.ok&&this.setModalStatus("Algo errado!","Se persistir, fale com o administrador!"),t.json().then((t=>{this.setModalStatus("Muito bom!","Salvo com sucesso!"),null!=t.redirect&&(window.location.href=t.redirect)}))})).catch((()=>{this.setModalStatus("Error grave!","Se persistir, fale com o administrador!")})).finally((()=>{this.modalStatus.show(),this.loading=!1,this.buttonLabel="Salvar"}))}}})))}));
